mapper.sql:
SELECT
     COUNT( 1 )
 FROM
     jr_work_arrange wa
 WHERE
     1 = 1

     And wa.start_date >= #{workArrange.signedDateFrom}


     And wa.start_date <= #{workArrange.signedDateTo}


     AND wa.odd_numbers = #{workArrange.oddNumbers}


     AND wa.person_liable = #{workArrange.personLiable}


     AND wa.work_type = #{workArrange.workType}


     AND wa.appraisal_status = #{workArrange.appraisalStatus}


     AND wa.arrange_state = #{workArrange.arrangeState}

SELECT wa.id, wa.odd_numbers, wa.start_date, wa.end_date, wa.work_type workType, wa.reminder_date, wa.duty_station, wa.job_content, wa.arrange_initiator, wa.person_liable, wa.arrange_supervisor, wa.arrange_state, wa.creation_time, wa.start_time, wa.completion_time, wa.appraisal_status, wa.assessment_comments, wa.assessment_time, wa.assessment_people FROM jr_work_arrange wa WHERE 1 = 1
And wa.start_date >= #{workArrange.signedDateFrom}
And wa.start_date <= #{workArrange.signedDateTo}
AND wa.odd_numbers = #{workArrange.oddNumbers}
AND wa.person_liable = #{workArrange.personLiable}
AND wa.work_type = #{workArrange.workType}
AND wa.appraisal_status = #{workArrange.appraisalStatus}
AND wa.arrange_state = #{workArrange.arrangeState}

mapper.update:
UPDATE jr_work_arrange wa
SET wa.arrange_state = #{stateParam}
WHERE  wa.id = #{id}

UPDATE jr_work_arrange wa
SET wa.arrange_state = #{stateParam} , wa.start_time = #{today}
WHERE  wa.id = #{id}

UPDATE jr_work_arrange wa
SET wa.arrange_state = #{stateParam} , wa.completion_time = #{today}
WHERE  wa.id = #{id}
