mapper.sql:
SELECT COUNT(1)
FROM jr_personnel_archives pe
WHERE 1 = 1

    AND pe.job_number = #{personnelArchives.jobNumber}


    AND pe.user_name = #{personnelArchives.userName}


    AND pe.department_id = #{personnelArchives.departmentId}


    AND pe.user_state = #{personnelArchives.userState}


    AND pe.duties_id = #{personnelArchives.dutiesId}


    AND pe.position_id = #{personnelArchives.positionId}

SELECT pe.job_number jobNumber, pe.user_name userName, d.dept_name deptName, pe.gender, pa.parameter_name dutiesName, par.parameter_name positionName, pe.education, pe.identity_ard identityArd, pe.entry_date entryDate, pe.quit_date quitDate, pe.user_state userState, pe.address, pe.social_security socialSecurity FROM jr_personnel_archives pe LEFT JOIN t_dept d ON ( pe.department_id = d.dept_id ) LEFT JOIN jr_personnel_parameters pa ON ( pe.duties_id = pa.id ) LEFT JOIN jr_personnel_parameters par ON ( pe.position_id = par.id ) WHERE 1 = 1
AND pe.job_number = #{personnelArchives.jobNumber}
AND pe.user_name = #{personnelArchives.userName}
AND d.dept_id = #{personnelArchives.departmentId}
AND pe.user_state = #{personnelArchives.userState}
AND pa.id = #{personnelArchives.dutiesId}
AND par.id = #{personnelArchives.positionId}
