mapper.sql:
SELECT COUNT(1)
FROM jr_personnel_archives pe
WHERE 1 = 1

    AND pe.job_number = #{personnelArchives.jobNumber}


    AND pe.user_name = #{personnelArchives.userName}


    AND pe.dept_id = #{personnelArchives.deptId}


    AND pe.user_state = #{personnelArchives.userState}


    AND pe.duties_id = #{personnelArchives.dutiesId}


    AND pe.position_id = #{personnelArchives.positionId}

SELECT pe.user_id userId, pe.job_number jobNumber, pe.user_name userName, d.dept_name deptName, pe.gender, pa.parameter_name dutiesName, par.parameter_name positionName, pas.parameter_name educationName, pe.identity_ard identityArd, pe.entry_date entryDate, pe.quit_date quitDate, pe.user_state userState, pe.address, GROUP_CONCAT(pp.id) parametersId, GROUP_CONCAT(pp.parameter_name) parameterName FROM jr_personnel_archives pe LEFT JOIN t_dept d ON ( pe.dept_id = d.dept_id ) LEFT JOIN jr_personnel_parameters pa ON ( pe.duties_id = pa.id ) LEFT JOIN jr_personnel_parameters par ON ( pe.position_id = par.id ) LEFT JOIN jr_personnel_parameters pas ON ( pe.education_id = pas.id ) LEFT JOIN jr_archives_parameters ap ON (pe.user_id = ap.archives_id) LEFT JOIN jr_personnel_parameters pp ON pp.id = ap.parameters_id WHERE 1 = 1
AND pe.job_number = #{personnelArchives.jobNumber}
AND pe.user_name = #{personnelArchives.userName}
AND d.dept_id = #{personnelArchives.deptId}
AND pe.user_state = #{personnelArchives.userState}
AND pa.id = #{personnelArchives.dutiesId}
AND par.id = #{personnelArchives.positionId}
GROUP BY pe.entry_date
