mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_personnel_dormitory_information pdi
WHERE
    1 = 1

    AND pdi.dormitory_no = #{personnelDormitoryInformation.dormitoryNo}


    AND pdi.dormitory_location = #{personnelDormitoryInformation.dormitoryLocation}


    AND pdi.employee_name = #{personnelDormitoryInformation.employeeName}

SELECT pdi.id, pdi.dormitory_no dormitoryNo, pdi.dormitory_location dormitoryLocation, pdi.used_beds usedBeds, pdi.employee_name employeeName, pdi.employee_telephone employeeTelephone, pdi.employee_dept employeeDept, pdi.check_in_time checkInTime FROM jr_personnel_dormitory_information pdi WHERE 1 = 1
AND pdi.dormitory_no = #{personnelDormitoryInformation.dormitoryNo}
AND pdi.dormitory_location = #{personnelDormitoryInformation.dormitoryLocation}
AND pdi.employee_name = #{personnelDormitoryInformation.employeeName}

SELECT
COUNT( 1 )
FROM
jr_personnel_dormitory pd
WHERE
pd.dormitory_state = 1

    AND pd.dormitory_no = #{personnelDormitory.dormitoryNo}


    AND pd.dormitory_place = #{personnelDormitory.dormitoryPlace}


    AND pd.dormitory_state = #{personnelDormitory.dormitoryState}

SELECT pd.id, pd.dormitory_no dormitoryNo, pd.dormitory_place dormitoryPlace, pd.reside_nnt resideNnt, pd.present_nnt presentNnt, pd.dormitory_administrators dormitoryAdministrators, pd.create_date createDate, pd.dormitory_state dormitoryState FROM jr_personnel_dormitory pd WHERE pd.dormitory_state = 1
AND pd.dormitory_no = #{personnelDormitory.dormitoryNo}
AND pd.dormitory_place = #{personnelDormitory.dormitoryPlace}
AND pd.dormitory_state = #{personnelDormitory.dormitoryState}
GROUP BY pd.dormitory_place,pd.dormitory_no

mapper.update:
UPDATE jr_personnel_dormitory_information pdi
SET pdi.used_beds = #{usedBedsCount}
WHERE pdi.id = #{id}

UPDATE jr_personnel_dormitory_information pdi
SET pdi.used_beds = #{usedBedsCount}
  WHERE
    pdi.dormitory_no = #{dormitoryNo}
  AND
    pdi.dormitory_location = #{dormitoryPlace}
