mapper.sql:
SELECT
COUNT( 1 )
FROM
jr_personnel_dormitory pd
WHERE
1 = 1

    AND pd.dormitory_no = #{personnelDormitory.dormitoryNo}


    AND pd.dormitory_place = #{personnelDormitory.dormitoryPlace}


    AND pd.dormitory_state = #{personnelDormitory.dormitoryState}

SELECT pd.id, pd.dormitory_no dormitoryNo, pd.dormitory_place dormitoryPlace, pd.reside_nnt resideNnt, pd.present_nnt presentNnt, pd.dormitory_administrators dormitoryAdministrators, pd.create_date createDate, pd.dormitory_state dormitoryState FROM jr_personnel_dormitory pd WHERE 1 = 1
AND pd.dormitory_no = #{personnelDormitory.dormitoryNo}
AND pd.dormitory_place = #{personnelDormitory.dormitoryPlace}
AND pd.dormitory_state = #{personnelDormitory.dormitoryState}
GROUP BY pd.dormitory_place,pd.dormitory_no
