mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_personnel_mobility pm
WHERE
    1 = 1

    AND pm.job_number = #{personnelMobility.jobNumber}


    AND pm.user_name = #{personnelMobility.userName}


    AND pm.transfer_status = #{personnelMobility.transferStatus}

SELECT pm.id, pm.user_id userId, pm.job_number jobNumber, pm.user_name userName, pm.gender, pm.birthdate, pm.phone, pm.identity_ard identityArd, pm.department_name departmentName, pm.position_name positionName, pm.grouping_name groupingName, d.DEPT_NAME toDepartmentName, pa.parameter_name toPositionName, par.parameter_name toGroupingName, pm.transfer_data transferData, pm.transfer_status transferStatus FROM jr_personnel_mobility pm LEFT JOIN t_dept d ON ( pm.dept_id = d.dept_id ) LEFT JOIN jr_personnel_parameters pa ON ( pm.to_position_id = pa.id ) LEFT JOIN jr_personnel_parameters par ON ( pm.to_grouping_id = par.id ) WHERE 1 = 1
AND pm.job_number = #{personnelMobility.jobNumber}
AND pm.user_name = #{personnelMobility.userName}
AND pm.transfer_status = #{personnelMobility.transferStatus}

SELECT COUNT(1)
FROM jr_personnel_archives pe
WHERE 1 = 1

    AND pe.job_number = #{personnelArchives.jobNumber}


    AND pe.user_name = #{personnelArchives.userName}


    AND pe.user_state = #{personnelArchives.userState}

SELECT pe.user_id userId, pe.job_number jobNumber, pe.user_name userName, d.dept_name deptName, pe.phone, pe.gender, pa.parameter_name dutiesName, par.parameter_name positionName, pe.birthdate, pe.identity_ard identityArd, pe.entry_date entryDate, pe.user_state userState FROM jr_personnel_archives pe LEFT JOIN t_dept d ON ( pe.dept_id = d.dept_id ) LEFT JOIN jr_personnel_parameters pa ON ( pe.duties_id = pa.id ) LEFT JOIN jr_personnel_parameters par ON ( pe.position_id = par.id ) WHERE 1 = 1
AND pe.job_number = #{personnelArchives.jobNumber}
AND pe.user_name = #{personnelArchives.userName}
AND pe.user_state = #{personnelArchives.userState}
GROUP BY pe.user_name,pe.job_number
