mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_production_plan pp
WHERE
    1 = 1

    And pp.plan_voucher_date >= #{productionPlan.signedDateFrom}


    And pp.plan_voucher_date <= #{productionPlan.signedDateTo}


    AND pp.plan_state = #{productionPlan.planState}


    AND pp.plan_customer_name = #{productionPlan.planCustomerName}


    AND pp.plan_number = #{productionPlan.planNumber}


    AND pp.plan_code = #{productionPlan.planCode}


    AND pp.plan_product_name = #{productionPlan.planProductName}


    AND pp.plan_specifications = #{productionPlan.planSpecifications}


    AND pp.plan_head = #{productionPlan.planHead} or pp.plan_head_one = #{productionPlan.planHead}


    And pp.plan_actual_date >= #{productionPlan.signedDateFromOne} or pp.plan_actual_date_one >= #{productionPlan.signedDateFromOne}


    And pp.plan_actual_date <= #{productionPlan.signedDateToOne} or pp.plan_actual_date_one >= #{productionPlan.signedDateFromOne}

SELECT pp.id, pp.plan_state, pp.plan_date, pp.plan_number, pp.plan_customer_name, pp.plan_product_name, pp.plan_specifications, pp.plan_code, pp.plan_configuration, pp.plan_machine_bom, pp.plan_invoice, pp.plan_factory, pp.plan_machining, pp.plan_assembly, pp.plan_instructions_one, pp.plan_instructions_two, pp.plan_instructions_three, pp.plan_voucher, pp.plan_voucher_date, pp.plan_note, pp.plan_money, pp.plan_head, pp.plan_expect_date, pp.plan_actual_date, pp.plan_wage, pp.plan_head_one, pp.plan_expect_date_one, pp.plan_actual_date_one, pp.plan_wage_one, pp.plan_content, pp.plan_unit, pps.plan_amount FROM jr_production_plan pp LEFT JOIN jr_production_plan_schedule pps ON pp.plan_code = pps.plan_code WHERE 1 = 1
And pp.plan_date >= #{productionPlan.signedDateFrom}
And pp.plan_date <= #{productionPlan.signedDateTo}
AND pp.plan_state = #{productionPlan.planState}
AND pp.plan_customer_name = #{productionPlan.planCustomerName}
AND pp.plan_number = #{productionPlan.planNumber}
AND pp.plan_code = #{productionPlan.planCode}
AND pp.plan_product_name = #{productionPlan.planProductName}
AND pp.plan_specifications = #{productionPlan.planSpecifications}
AND pp.plan_head = #{productionPlan.planHead} or pp.plan_head_one = #{productionPlan.planHead}
And pp.plan_actual_date >= #{productionPlan.signedDateFromOne} or pp.plan_actual_date_one >= #{productionPlan.signedDateFromOne}
And pp.plan_actual_date <= #{productionPlan.signedDateToOne} or pp.plan_actual_date_one >= #{productionPlan.signedDateFromOne}

mapper.select:
SELECT pp.id, pp.plan_state, pp.plan_date, pp.plan_number, pp.plan_customer_name, pp.plan_product_name, pp.plan_specifications, pp.plan_code, pp.plan_configuration, pp.plan_machine_bom, pp.plan_invoice, pp.plan_factory, pp.plan_machining, pp.plan_assembly, pp.plan_instructions_one, pp.plan_instructions_two, pp.plan_instructions_three, pp.plan_voucher, pp.plan_voucher_date, pp.plan_note, pp.plan_money, pp.plan_head, pp.plan_expect_date, pp.plan_actual_date, pp.plan_wage, pp.plan_head_one, pp.plan_expect_date_one, pp.plan_actual_date_one, pp.plan_wage_one, pp.plan_content, pp.plan_unit, pps.plan_amount FROM jr_production_plan pp LEFT JOIN jr_production_plan_schedule pps ON pp.plan_code = pps.plan_code WHERE 1 = 1
And pp.plan_date >= #{productionPlan.signedDateFrom}
And pp.plan_date <= #{productionPlan.signedDateTo}
AND pp.plan_state = #{productionPlan.planState}
AND pp.plan_customer_name = #{productionPlan.planCustomerName}
AND pp.plan_number = #{productionPlan.planNumber}
AND pp.plan_code = #{productionPlan.planCode}
AND pp.plan_product_name = #{productionPlan.planProductName}
AND pp.plan_specifications = #{productionPlan.planSpecifications}
AND pp.plan_head = #{productionPlan.planHead} or pp.plan_head_one = #{productionPlan.planHead}

mapper.update:
UPDATE
    jr_production_plan
set plan_configuration= #{productionPlan.planConfiguration},plan_note = #{productionPlan.planNote}
WHERE plan_number=#{productionPlan.planNumber}

UPDATE
    jr_production_plan
set plan_factory= #{productionPlan.planFactory}
WHERE plan_number=#{productionPlan.planNumber}

UPDATE
    jr_production_plan
set plan_machine_bom= #{productionPlan.planMachineBom},plan_machine_bom_date= #{productionPlan.planMachineBomDate}
WHERE plan_code=#{productionPlan.planCode}

UPDATE
    jr_production_plan
set plan_head= #{productionPlan.planHead},plan_expect_date= #{productionPlan.planExpectDate},plan_actual_date= #{productionPlan.planActualDate},
    plan_wage= #{productionPlan.planWage},plan_head_one= #{productionPlan.planHeadOne},plan_expect_date_one= #{productionPlan.planExpectDateOne},
    plan_actual_date_one= #{productionPlan.planActualDateOne},plan_wage_one= #{productionPlan.planWageOne},plan_content= #{productionPlan.planContent}
WHERE id=#{productionPlan.id}
