SELECT
    COUNT( 1 )
FROM
    jr_purchase_inspection pi
LEFT JOIN jr_purchase_inspection_schedule pis ON ( pi.inspection_number = pis.inspection_number )
WHERE
    1 = 1

        And pi.purchase_requisition_date >= #{purchaseInspection.signedDateFrom}


        And pi.purchase_requisition_date <= #{purchaseInspection.signedDateTo}


        AND pi.supplier_name = #{purchaseInspection.supplierName}


        AND pis.inspection_state = #{purchaseInspection.inspectionState}


        AND pi.inspection_number = #{purchaseInspection.inspectionNumber}


        AND pi.inspection_type = #{purchaseInspection.inspectionType}

SELECT pi.id, pis.id ids, pis.inspection_state, pi.purchase_requisition_date, pi.inspection_number, pi.supplier_name, pi.inspection_type, pis.order_quantity, pi.inspection_preparer, pi.inspection_preparation_date, pis.inspection_remarks, pis.material_name, pis.inspectionr_specifications, pis.inspection_qualified, pis.inspection_quality, pis.inspection_quality_date, pis.inspection_library, pis.inspection_library_date, pis.inspection_library_code, pis.inspection_describe FROM jr_purchase_inspection pi LEFT JOIN jr_purchase_inspection_schedule pis ON ( pi.inspection_number = pis.inspection_number ) WHERE 1 = 1
And pi.purchase_requisition_date >= #{purchaseInspection.signedDateFrom}
And pi.purchase_requisition_date <= #{purchaseInspection.signedDateTo}
AND pi.supplier_name = #{purchaseInspection.supplierName}
AND pis.inspection_state = #{purchaseInspection.inspectionState}
AND pi.inspection_number = #{purchaseInspection.inspectionNumber}
AND pi.inspection_type = #{purchaseInspection.inspectionType}

SELECT pi.supplier_name, pis.order_quantity, pis.material_name, pis.inspectionr_specifications, pis.inspection_qualified FROM jr_purchase_inspection pi LEFT JOIN jr_purchase_inspection_schedule pis ON ( pi.inspection_number = pis.inspection_number ) WHERE 1 = 1 AND (pis.inspection_state = '3' or pis.inspection_state = '4')
And pi.purchase_requisition_date >= #{purchaseInspection.signedDateFrom}
And pi.purchase_requisition_date <= #{purchaseInspection.signedDateTo}
AND pi.supplier_name = #{purchaseInspection.supplierName}
AND pis.material_name = #{purchaseInspection.materialName}
AND pis.inspectionr_specifications = #{purchaseInspection.inspectionrSpecifications}

SELECT
    COUNT( 1 )
FROM
    jr_purchase_inspection pi
    LEFT JOIN jr_purchase_inspection_schedule pis ON ( pi.inspection_number = pis.inspection_number )
WHERE
     1 = 1
AND (pis.inspection_state = '3' or pis.inspection_state = '4')

    And pi.purchase_requisition_date >= #{purchaseInspection.signedDateFrom}


    And pi.purchase_requisition_date <= #{purchaseInspection.signedDateTo}


    AND pi.supplier_name = #{purchaseInspection.supplierName}


    AND pis.material_name = #{purchaseInspection.materialName}


    AND pis.inspectionr_specifications = #{purchaseInspection.inspectionrSpecifications}
