mapper.sql:
SELECT pi.id, sum(pis.invoice_quantity) invoiceQuantitySum, sum(pis.tax_money)taxMoneySum, pis.invoice_amount, pi.invoice_state, pi.invoice_date, pi.invoice_numbers, pi.invoice_supplier, sum(pis.invoice_money) invoiceMoney, fpa.finance_parameter_name currencyName, fpb.finance_parameter_name taxRateName, pi.tax_rate_money, pi.registration, pi.registration_date, pi.invoice_remarks FROM jr_purchase_invoice pi LEFT JOIN jr_purchase_invoice_schedule pis ON ( pi.invoice_numbers = pis.invoice_numbers ) LEFT JOIN jr_finance_parameters fpa ON ( pi.currency_id = fpa.id ) LEFT JOIN jr_finance_parameters fpb ON ( pi.tax_rate_id = fpb.id ) WHERE 1 = 1
AND pi.invoice_state = #{purchaseInvoice.invoiceState}
AND pi.invoice_numbers = #{purchaseInvoice.invoiceNumbers}
AND pi.invoice_supplier = #{purchaseInvoice.invoiceSupplier}
AND pi.currency_id = #{purchaseInvoice.currencyId}
AND pi.tax_rate_id = #{purchaseInvoice.taxRateId}
GROUP BY pi.invoice_numbers
