mapper.sql:
SELECT COUNT( 1 ) FROM jr_purchase_material_category pmc WHERE 1 = 1
AND pmc.category_name like CONCAT('%',#{purchaseMaterialCategory.categoryName},'%')
AND pmc.material_category = #{purchaseMaterialCategory.materialCategory}
AND pmc.material_category_state = #{purchaseMaterialCategory.materialCategoryState}

SELECT pmc.id, pmc.material_category_state materialCategoryState, pmc.material_category materialCategory, pmca.category_name belongingGeneralCategory, pmc.category_name categoryName, pmc.material_category_sort materialCategorySort, pmc.material_category_remarks materialCategoryRemarks FROM jr_purchase_material_category pmc LEFT JOIN jr_purchase_material_category pmca ON ( pmc.belonging_general_category_id = pmca.id ) WHERE 1 = 1
AND pmc.category_name like CONCAT('%',#{purchaseMaterialCategory.categoryName},'%')
AND pmc.material_category = #{purchaseMaterialCategory.materialCategory}
AND pmc.material_category_state = #{purchaseMaterialCategory.materialCategoryState}
