mapper.sql:
SELECT pr.id, pr.refund_state, pr.refund_date, pr.refund_number, pr.supplier_name, pr.refund_library, sum(prs.refund_quantity) refundQuantity, sum(prs.refund_money) refundMoney, pr.refund_preparer, pr.refund_preparation_date, pr.refund_outbound, pr.refund_outbound_date, pr.order_number, fpa.finance_parameter_name currencyName, fpb.finance_parameter_name taxRateName FROM jr_purchase_refund pr LEFT JOIN jr_purchase_refund_schedule prs ON ( pr.refund_number = prs.refund_number ) LEFT JOIN jr_finance_parameters fpa ON ( pr.currency_id = fpa.id ) LEFT JOIN jr_finance_parameters fpb ON ( pr.tax_rate_id = fpb.id ) WHERE 1 = 1
And pr.refund_date >= #{purchaseRefund.signedDateFrom}
And pr.refund_date <= #{purchaseRefund.signedDateTo}
AND pr.refund_state = #{purchaseRefund.refundState}
AND pr.refund_number = #{purchaseRefund.refundNumber}
AND pr.supplier_name = #{purchaseRefund.supplierNameRefund}
AND pr.refund_library = #{purchaseRefund.refundLibrary}
GROUP BY pr.refund_number

mapper.select:
SELECT ws.supplier_name, ws.order_number, ws.storage_library, ws.storage_numbers, wss.storage_code, wss.storage_name, wss.storage_specifications, wss.storage_quality, wss.storage_company, wss.storage_quantity, wss.unit_price, wss.storage_money, wss.storage_remarks, wss.storage_brand, wss.storage_subclass, wss.storage_category, wss.storage_deposit FROM jr_warehouse_storage ws LEFT JOIN jr_warehouse_storage_schedule wss ON ( ws.order_number = wss.order_numbers ) WHERE 1 = 1
AND ws.supplier_name = #{warehouseStorage.supplierName}
AND ws.storage_library = #{warehouseStorage.storageLibrary}
AND ws.storage_numbers = #{warehouseStorage.storageNumbers}
AND wss.storage_code = #{warehouseStorage.storageCode}
AND wss.storage_name = #{warehouseStorage.storageName}
AND wss.storage_specifications = #{warehouseStorage.storageSpecifications}
AND wss.storage_subclass = #{warehouseStorage.storageSubclass}
