mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_purchase_requisition pr
LEFT JOIN t_dept d ON ( pr.department_id = d.dept_id )
LEFT JOIN jr_purchase_requisition_schedule prs ON ( pr.odd_numbers = prs.odd_numbers )
WHERE
    1 =1

        And pr.purchase_requisition_date >= #{purchaseRequisition.signedDateFrom}


        And pr.purchase_requisition_date <= #{purchaseRequisition.signedDateTo}


        AND pr.odd_numbers = #{purchaseRequisition.oddNumbers}


        AND prs.apply_name = #{purchaseRequisition.applyName}


        AND prs.apply_specifications = #{purchaseRequisition.applySpecifications}


        AND prs.apply_cancel = #{purchaseRequisition.applyCancel}


        AND d.DEPT_NAME = #{purchaseRequisition.departmentName}


        AND prs.apply_subclass = #{purchaseRequisition.applySubclass}

SELECT pr.id, pr.apply_state applyState, pr.odd_numbers oddNumbers, pr.purchase_requisition_date purchaseRequisitionDate, d.DEPT_NAME departmentName, pr.apply_preparer applyPreparer, pr.apply_preparation_date applyPreparationDate, sum( prs.apply_quantity ) applyQuantity FROM jr_purchase_requisition pr LEFT JOIN t_dept d ON ( pr.department_id = d.dept_id ) LEFT JOIN jr_purchase_requisition_schedule prs ON ( pr.odd_numbers = prs.odd_numbers ) WHERE 1 = 1
And pr.purchase_requisition_date >= #{purchaseRequisition.signedDateFrom}
And pr.purchase_requisition_date <= #{purchaseRequisition.signedDateTo}
AND pr.odd_numbers = #{purchaseRequisition.oddNumbers}
AND pr.department_id = #{purchaseRequisition.departmentId}
AND pr.apply_state = #{purchaseRequisition.applyState}
GROUP BY oddNumbers

SELECT prs.id, pr.odd_numbers oddNumbers, pr.purchase_requisition_date purchaseRequisitionDate, d.DEPT_NAME departmentName, prs.apply_code applyCode, prs.apply_name applyName, prs.apply_specifications applySpecifications, prs.apply_brand applyBrand, prs.apply_quantity applyQuantity, prs.apply_company applyCompany, prs.apply_category applyCategory, prs.apply_subclass applySubclass, prs.apply_cancel applyCancel, prs.apply_remarks applyRemarks FROM jr_purchase_requisition pr LEFT JOIN t_dept d ON ( pr.department_id = d.dept_id ) LEFT JOIN jr_purchase_requisition_schedule prs ON ( pr.odd_numbers = prs.odd_numbers ) WHERE 1 =1
And pr.purchase_requisition_date >= #{purchaseRequisition.signedDateFrom}
And pr.purchase_requisition_date <= #{purchaseRequisition.signedDateTo}
AND pr.odd_numbers = #{purchaseRequisition.oddNumbers}
AND prs.apply_name = #{purchaseRequisition.applyName}
AND prs.apply_specifications = #{purchaseRequisition.applySpecifications}
AND prs.apply_cancel = #{purchaseRequisition.applyCancel}
AND d.DEPT_NAME = #{purchaseRequisition.departmentName}
AND prs.apply_subclass = #{purchaseRequisition.applySubclass}
