SELECT
    COUNT( 1 )
FROM
    jr_purchase_settlement ps
WHERE
    1 = 1

    AND ps.settlement_state = #{purchaseSettlement.settlementState}


    AND ps.settlement_numbers = #{purchaseSettlement.settlementNumbers}


    AND ps.settlement_supplier = #{purchaseSettlement.settlementSupplier}


    AND ps.settlement_library = #{purchaseSettlement.settlementLibrary}


    AND ps.settlement_use = #{purchaseSettlement.settlementUse}


    And ps.settlement_date >= #{purchaseSettlement.signedDateFrom}


    And ps.settlement_date <= #{purchaseSettlement.signedDateTo}

SELECT ps.id, ps.settlement_state, ps.settlement_date, ps.settlement_numbers, ps.settlement_use, ps.settlement_supplier, wl.location_name settlementLibrary, sum(pss.settlement_quantity) settlementQuantity, sum(pss.settlement_money) settlementMoney, ps.settlement_invoice, fpa.finance_parameter_name currencyName, fpb.finance_parameter_name taxRateName, sum(pss.settlement_money) / ( 1 + fpb.finance_parameter_name * 0.01) settlementNoMoney FROM jr_purchase_settlement ps LEFT JOIN jr_purchase_settlement_schedule pss ON ( ps.settlement_numbers = pss.settlement_number ) LEFT JOIN jr_finance_parameters fpa ON ( ps.currency_id = fpa.id ) LEFT JOIN jr_finance_parameters fpb ON ( ps.tax_rate_id = fpb.id ) LEFT JOIN jr_warehouse_location wl ON ( ps.settlement_library = wl.location_sorting ) WHERE 1 = 1
AND ps.settlement_state = #{purchaseSettlement.settlementState}
AND ps.settlement_numbers = #{purchaseSettlement.settlementNumbers}
AND ps.settlement_supplier = #{purchaseSettlement.settlementSupplier}
AND ps.settlement_library = #{purchaseSettlement.settlementLibrary}
AND ps.settlement_use = #{purchaseSettlement.settlementUse}
And ps.settlement_date >= #{purchaseSettlement.signedDateFrom}
And ps.settlement_date <= #{purchaseSettlement.signedDateTo}
GROUP BY ps.settlement_numbers

SELECT
    COUNT( 1 )
FROM
    jr_purchase_settlement_schedule pss
        LEFT JOIN jr_purchase_settlement ps ON ( ps.settlement_numbers = pss.settlement_number )
        LEFT JOIN jr_warehouse_location wl ON ( ps.settlement_library = wl.location_sorting )
        LEFT JOIN jr_finance_parameters fpa ON ( ps.currency_id = fpa.id )
WHERE
    1 = 1

    And ps.settlement_date >= #{purchaseSettlementSchedule.signedDateFrom}


    And ps.settlement_date <= #{purchaseSettlementSchedule.signedDateTo}


    AND pss.settlement_number = #{purchaseSettlementSchedule.settlementNumber}


    AND ps.settlement_supplier = #{purchaseSettlementSchedule.settlementSupplier}


    AND ps.settlement_library = #{purchaseSettlementSchedule.settlementLibrary}


    AND ps.settlement_use = #{purchaseSettlementSchedule.settlementUse}


    AND ps.currency_id = #{purchaseSettlementSchedule.currencyId}


    AND pss.settlement_specifications = #{purchaseSettlementSchedule.settlementSpecifications}


    AND pss.settlement_name = #{purchaseSettlementSchedule.settlementName}

SELECT ps.settlement_date, pss.settlement_number, ps.settlement_use, ps.settlement_supplier, wl.location_name settlementLibrary, pss.settlement_code, pss.settlement_name, pss.settlement_specifications, pss.settlement_brand, pss.settlement_company, fpa.finance_parameter_name currencyName, pss.settlement_quantity, pss.settlement_price, pss.settlement_money, pss.settlement_remarks FROM jr_purchase_settlement_schedule pss LEFT JOIN jr_purchase_settlement ps ON ( ps.settlement_numbers = pss.settlement_number ) LEFT JOIN jr_warehouse_location wl ON ( ps.settlement_library = wl.location_sorting ) LEFT JOIN jr_finance_parameters fpa ON ( ps.currency_id = fpa.id ) WHERE 1 = 1
And ps.settlement_date >= #{purchaseSettlementSchedule.signedDateFrom}
And ps.settlement_date <= #{purchaseSettlementSchedule.signedDateTo}
AND pss.settlement_number = #{purchaseSettlementSchedule.settlementNumber}
AND ps.settlement_supplier = #{purchaseSettlementSchedule.settlementSupplier}
AND ps.settlement_library = #{purchaseSettlementSchedule.settlementLibrary}
AND ps.settlement_use = #{purchaseSettlementSchedule.settlementUse}
AND ps.currency_id = #{purchaseSettlementSchedule.currencyId}
AND pss.settlement_specifications = #{purchaseSettlementSchedule.settlementSpecifications}
AND pss.settlement_name = #{purchaseSettlementSchedule.settlementName}

SELECT
    COUNT( 1 )
FROM
    jr_purchase_settlement_schedule pss
        LEFT JOIN jr_purchase_settlement ps ON ( ps.settlement_numbers = pss.settlement_number )
        LEFT JOIN jr_purchase_invoice_schedule pis ON ( pis.invoice_code = pss.settlement_number )
WHERE
    1 = 1

    And ps.settlement_date >= #{purchaseInvoiceStatistical.signedDateFrom}



    And ps.settlement_date <= #{purchaseInvoiceStatistical.signedDateTo}


    AND pss.whether_invoice = #{purchaseInvoiceStatistical.whetherInvoice}


    AND pss.settlement_number = #{purchaseInvoiceStatistical.settlementNumber}


    AND pss.settlement_specifications = #{purchaseInvoiceStatistical.settlementSpecifications}


    AND pss.settlement_subclass = #{purchaseInvoiceStatistical.settlementSubclass}


    AND ps.settlement_supplier = #{purchaseInvoiceStatistical.settlementSupplier}

SELECT ps.settlement_date, pis.invoice_numbers, ps.settlement_supplier, pss.settlement_code, pss.settlement_name, pss.settlement_specifications, pss.settlement_quantity, pis.invoice_amount, (pis.invoice_amount - pss.settlement_quantity) notInvoice, pss.settlement_company, pss.settlement_price, pss.settlement_money FROM jr_purchase_settlement_schedule pss LEFT JOIN jr_purchase_settlement ps ON ( ps.settlement_numbers = pss.settlement_number ) LEFT JOIN jr_purchase_invoice_schedule pis ON ( pis.invoice_code = pss.settlement_number ) WHERE 1 = 1
And ps.settlement_date >= #{purchaseInvoiceStatistical.signedDateFrom}
And ps.settlement_date <= #{purchaseInvoiceStatistical.signedDateTo}
AND pss.whether_invoice = #{purchaseInvoiceStatistical.whetherInvoice}
AND pss.settlement_name = #{purchaseInvoiceStatistical.settlementName}
AND pss.settlement_specifications = #{purchaseInvoiceStatistical.settlementSpecifications}
AND pss.settlement_subclass = #{purchaseInvoiceStatistical.settlementSubclass}
AND ps.settlement_supplier = #{purchaseInvoiceStatistical.settlementSupplier}
