mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_sale_application sa
WHERE
    1 = 1

    And sa.requested_delivery_date > #{saleApplication.signedDateFrom}


    And sa.requested_delivery_date < #{saleApplication.signedDateTo}


    And sa.customer_name = #{saleApplication.customerName}


    And sa.salesman_name = #{saleApplication.salesmanName}


    And sa.application_no = #{saleApplication.applicationNo}


    And sa.product_name = #{saleApplication.productName}


    And sa.specification_model = #{saleApplication.specificationModel}

SELECT sa.id, sa.requested_delivery_date requestedDeliveryDate, sa.application_no applicationNo, sa.customer_id customerId, sa.customer_name customerName, sa.salesman_name salesmanName, sa.product_name productName, sa.specification_model specificationModel, sa.configure_name configureName, sa.company_name companyName, sa.quantity_name quantityName, sa.enclosure_name enclosureName, sa.design_date designDate, sa.design_reply designReply, sa.purchase_date purchaseDate, sa.purchase_reply purchaseReply, sa.production_date productionDate, sa.production_reply productionReply, sa.assembling_date assemblingDate, sa.assembling_reply assemblingReply, sa.explain_name explainName, sa.machine_requirements machineRequirements, sa.computer_configuration computerConfiguration, sa.tool_size toolSize, sa.hourly_production hourlyProduction, sa.processing_procedure processingProcedure, sa.fixture_requirements fixtureRequirements, sa.product_shape productShape, sa.product_size productSize, sa.other_requirements otherRequirements FROM jr_sale_application sa WHERE 1 = 1
And sa.requested_delivery_date > #{saleApplication.signedDateFrom}
And sa.requested_delivery_date < #{saleApplication.signedDateTo}
And sa.customer_name = #{saleApplication.customerName}
And sa.salesman_name = #{saleApplication.salesmanName}
And sa.application_no = #{saleApplication.applicationNo}
And sa.product_name = #{saleApplication.productName}
And sa.specification_model = #{saleApplication.specificationModel}
