mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_sale_customer_profile scp
WHERE
    1 = 1

    AND scp.personnel_name = #{saleCustomerProfile.personnelName}


    AND scp.customer_full_name = #{saleCustomerProfile.customerFullName}


    AND scp.customer_abbreviation = #{saleCustomerProfile.customerAbbreviation}


    AND scp.customer_code = #{saleCustomerProfile.customerCode}


    AND scp.customer_contacts = #{saleCustomerProfile.customerContacts}


    AND scp.contact_number = #{saleCustomerProfile.contactNumber}


    AND scp.customer_state = #{saleCustomerProfile.customerState}


    And scp.created_date > #{saleCustomerProfile.createdDateFrom}


    And scp.created_date < #{saleCustomerProfile.createdDateTo}


    And getPY(scp.customer_full_name) like CONCAT('%',#{saleCustomerProfile.abbreviationOfFullName,jdbcType=VARCHAR},'%')

SELECT scp.id, scp.personnel_id personnelId, scp.personnel_name personnelName, scp.customer_full_name customerFullName, scp.customer_abbreviation customerAbbreviation, scp.work_telephone workTelephone, scp.unit_fax unitFax, scp.customer_contacts customerContacts, scp.contact_number contactNumber, scp.customer_mail customerMail, scp.unit_address unitAddress, scp.customer_state customerState, scp.customer_remarks customerRemarks, scp.bank_of_deposit bankOfDeposit, scp.bank_account bankAccount, scp.unit_tax_no unitTaxNo, scp.billing_telephone billingTelephone, scp.billing_address billingAddress, scp.customer_consignee customerConsignee, scp.receiving_telephone receivingTelephone, scp.postal_code postalCode, scp.receiving_address receivingAddress, scp.created_date createdDate, scp.customer_code customerCode FROM jr_sale_customer_profile scp WHERE 1 = 1
AND scp.personnel_name = #{saleCustomerProfile.personnelName}
AND scp.customer_full_name = #{saleCustomerProfile.customerFullName}
AND scp.customer_abbreviation = #{saleCustomerProfile.customerAbbreviation}
AND scp.customer_code = #{saleCustomerProfile.customerCode}
AND scp.customer_contacts = #{saleCustomerProfile.customerContacts}
AND scp.contact_number = #{saleCustomerProfile.contactNumber}
AND scp.customer_state = #{saleCustomerProfile.customerState}
And scp.created_date > #{saleCustomerProfile.createdDateFrom}
And scp.created_date < #{saleCustomerProfile.createdDateTo}
And getPY(scp.customer_full_name) like CONCAT('%',#{saleCustomerProfile.abbreviationOfFullName,jdbcType=VARCHAR},'%')
