mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_sale_order so
LEFT JOIN jr_sale_order_schedule sos ON ( so.odd_numbers = sos.odd_numbers )
LEFT JOIN jr_finance_parameters fpa ON ( so.currency_id = fpa.id )
LEFT JOIN jr_finance_parameters fpb ON ( so.tax_rate_id = fpb.id )
LEFT JOIN jr_finance_parameters fpc ON ( so.payment_method_id = fpc.id )
WHERE
    1 = 1

    And sos.order_state = #{saleOrder.orderState}


    And so.order_date > #{saleOrder.startTimeFrom}


    And so.order_date < #{saleOrder.endTimeTo}


    And so.customer_name = #{saleOrder.customerName}


    And so.salesman_name = #{saleOrder.salesmanName}


    And so.after_sales_clerk = #{saleOrder.afterSalesClerk}


    And so.odd_numbers = #{saleOrder.oddNumbers}


    And sos.product_name = #{saleOrder.productName}


    And sos.specification_model = #{saleOrder.specificationModel}


    And so.currency_id = #{saleOrder.currencyName}

SELECT so.id, sos.order_state orderState, so.odd_numbers oddNumbers, so.order_date orderDate, so.customer_name customerName, so.salesman_name salesmanName, fpa.finance_parameter_name currencyName, fpb.finance_parameter_name taxRate, fpc.finance_parameter_name paymentMethod, so.deposit_money depositMoney, so.invoice_not invoiceNot, so.contacts_name contactsName, so.mobile_phone mobilePhone, so.enclosure_name enclosureName, so.order_type orderType, so.after_sales_clerk afterSalesClerk, sos.id orderScheduleId, sos.product_name productName, sos.specification_model specificationModel, sos.configure_name configureName, sos.company_name companyName, sos.quantity_name quantityName, sos.unit_price unitPrice, sos.amount_money amountMoney, sos.delivery_date deliveryDate, sos.order_remarks orderRemarks, sos.machine_requirements machineRequirements, sos.computer_configuration computerConfiguration, sos.tool_size toolSize, sos.hourly_production hourlyProduction, sos.processing_procedure processingProcedure, sos.fixture_requirements fixtureRequirements, sos.product_shape productShape, sos.product_size productSize, sos.other_requirements otherRequirements, so.create_date createDate FROM jr_sale_order so LEFT JOIN jr_sale_order_schedule sos ON ( so.odd_numbers = sos.odd_numbers ) LEFT JOIN jr_finance_parameters fpa ON ( so.currency_id = fpa.id ) LEFT JOIN jr_finance_parameters fpb ON ( so.tax_rate_id = fpb.id ) LEFT JOIN jr_finance_parameters fpc ON ( so.payment_method_id = fpc.id ) WHERE 1 = 1
And sos.order_state = #{saleOrder.orderState}
And so.order_date > #{saleOrder.startTimeFrom}
And so.order_date < #{saleOrder.endTimeTo}
And so.customer_name = #{saleOrder.customerName}
And so.salesman_name = #{saleOrder.salesmanName}
And so.after_sales_clerk = #{saleOrder.afterSalesClerk}
And so.odd_numbers = #{saleOrder.oddNumbers}
And sos.product_name = #{saleOrder.productName}
And sos.specification_model = #{saleOrder.specificationModel}
And so.currency_id = #{saleOrder.currencyName}
