mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_sale_order so
WHERE
    1 = 1

    And so.order_date > #{saleOrder.startTimeFrom}


    And so.order_date < #{saleOrder.endTimeTo}


    And so.customer_name = #{saleOrder.customerName}


    And so.salesman_name = #{saleOrder.salesmanName}


    And so.after_sales_clerk = #{saleOrder.afterSalesClerk}


    And so.odd_numbers = #{saleOrder.oddNumbers}


    And so.product_name = #{saleOrder.productName}


    And so.specification_model = #{saleOrder.specificationModel}


    And so.currency_id = #{saleOrder.currencyName}

SELECT so.id, so.odd_numbers oddNumbers, so.order_date orderDate, so.customer_name customerName, so.salesman_name salesmanName, fpa.finance_parameter_name currencyName, fpb.finance_parameter_name taxRate, fpc.finance_parameter_name paymentMethod, so.deposit_money depositMoney, so.invoice_not invoiceNot, so.contacts_name contactsName, so.mobile_phone mobilePhone, so.enclosure_name enclosureName, so.order_type orderType, so.after_sales_clerk afterSalesClerk, so.product_name productName, so.specification_model specificationModel, so.configure_name configureName, so.company_name companyName, so.quantity_name quantityName, so.unit_price unitPrice, so.amount_money amountMoney, so.delivery_date deliveryDate, so.order_remarks orderRemarks, so.machine_requirements machineRequirements, so.computer_configuration computerConfiguration, so.tool_size toolSize, so.hourly_production hourlyProduction, so.processing_procedure processingProcedure, so.fixture_requirements fixtureRequirements, so.product_shape productShape, so.product_size productSize, so.other_requirements otherRequirements, so.create_date createDate FROM jr_sale_order so LEFT JOIN jr_finance_parameters fpa ON ( so.currency_id = fpa.id ) LEFT JOIN jr_finance_parameters fpb ON ( so.tax_rate_id = fpb.id ) LEFT JOIN jr_finance_parameters fpc ON ( so.payment_method_id = fpc.id ) WHERE 1 = 1
And so.order_date > #{saleOrder.startTimeFrom}
And so.order_date < #{saleOrder.endTimeTo}
And so.customer_name = #{saleOrder.customerName}
And so.salesman_name = #{saleOrder.salesmanName}
And so.after_sales_clerk = #{saleOrder.afterSalesClerk}
And so.odd_numbers = #{saleOrder.oddNumbers}
And so.product_name = #{saleOrder.productName}
And so.specification_model = #{saleOrder.specificationModel}
And so.currency_id = #{saleOrder.currencyName}
