mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_sale_parameters sp
WHERE
    1 = 1

    AND sp.parameter_name = #{saleParameters.parameterName}


    AND sp.parameter_category = #{saleParameters.parameterCategory}


    AND sp.parameter_state = #{saleParameters.parameterState}

SELECT sp.id, sp.parameter_category parameterCategory, sp.parameter_value parameterValue, sp.parameter_name parameterName, sp.parameter_sort parameterSort, sp.parameter_state parameterState, sp.parameter_remarks parameterRemarks FROM jr_sale_parameters sp WHERE 1 = 1
AND sp.parameter_name = #{saleParameters.parameterName}
AND sp.parameter_category = #{saleParameters.parameterCategory}
AND sp.parameter_state = #{saleParameters.parameterState}
ORDER BY sp.parameter_category, sp.parameter_sort
