mapper.sql:
SELECT COUNT( 1 ) FROM jr_technology_product tp LEFT JOIN jr_purchase_parameters pp ON ( tp.product_metering_company_id = pp.id ) WHERE 1 = 1
AND tp.product_name = #{saleProduct.productName}
AND tp.product_specification_model = #{saleProduct.specificationModel}

SELECT tp.id, tp.product_name productName, tp.product_specification_model specificationModel, tp.product_remarks productRemarks, pp.purchase_parameters_name purchaseParametersName, tp.product_state productState FROM jr_technology_product tp LEFT JOIN jr_purchase_parameters pp ON ( tp.product_metering_company_id = pp.id ) WHERE tp.product_state = 1
AND tp.product_name = #{saleProduct.productName}
AND tp.product_specification_model = #{saleProduct.specificationModel}
order by tp.id asc
