mapper.sql:
SELECT
    COUNT( 1 )
FROM
    jr_technology_bom_configuration tbc
WHERE
    1 = 1

        AND tbc.parameter_part = #{technologyBomConfiguration.parameterName}


        AND tbc.parameter_state = #{technologyBomConfiguration.parameterState}


        AND tbc.parameter_with = #{technologyBomConfiguration.parameterWith}

SELECT tbc.id, tbc.parameter_state, tbc.parameter_code, tbp.parameter_name parameterName, tbc.parameter_with, tbc.parameter_attachment, tbc.parameter_instructions, tbc.parameter_inputting, tbc.parameter_inputting_date FROM jr_technology_bom_configuration tbc LEFT JOIN jr_technology_bom_configuration_schedule tbcs ON tbc.parameter_code = tbcs.parameter_code LEFT JOIN jr_technology_bom_parameter tbp ON tbc.parameter_part = tbp.id WHERE 1 = 1
AND tbc.parameter_part = #{technologyBomConfiguration.parameterName}
AND tbc.parameter_state = #{technologyBomConfiguration.parameterState}
AND tbc.parameter_with = #{technologyBomConfiguration.parameterWith}
GROUP BY tbc.parameter_code
